module Ntt_TB;
parameter W = 16, Coeff = 256 ,N = $clog2(Coeff);

reg clk, rst;
wire done;
reg  [W*Coeff - 1 : 0] A,check;
wire [W*Coeff - 1 : 0] Output;

Ntt_256_top dut
(
    clk,
    rst,
    A,
    Output,
    done
);

always#5 clk = !clk;

initial begin

clk = 0;
rst = 1;
#20;

rst = 0;
A = 4096';

wait(done);
$display("output = %x", Output);
check = 4096';
if(check == Output) $display("PASSED");


#20;

$finish;

end


endmodule
